import React from 'react';
import './Modal.css'; // if you're still using it

export default function Modal({ imageSrc, title, description, onClose }) {
  if (!imageSrc) return null;

  return (
    <div
      className="fixed inset-0 bg-black bg-opacity-80 z-50 flex items-center justify-center px-4"
      onClick={onClose}
    >
      <div
        className="bg-white dark:bg-gray-800 p-4 rounded-lg max-w-3xl w-full shadow-lg"
        onClick={(e) => e.stopPropagation()}
      >
        <img
          src={imageSrc}
          alt={title}
          className="rounded w-full max-h-[60vh] object-contain mx-auto"
        />
        <div className="mt-4 text-center">
          <h3 className="text-xl font-bold text-gray-800 dark:text-white">{title}</h3>
          <p className="text-gray-600 dark:text-gray-300 mt-2">{description}</p>
        </div>
      </div>
    </div>
  );
}
